                $lokasi = "'" . implode ( "', '", $_POST['lokasi'] ) . "'";
                $qweloc = "WHERE worklocation IN ($lokasi)";
            }

            $query = "SELECT * FROM pegawai $qweloc";
            $result = mysqli_query($conn, $query);                                  
            while ($data = mysqli_fetch_array($result)) {

                echo "<tr>";
                echo "<td>$data[worklocation]</td>";
                echo "<td>$data[nip]</td>";
                echo "<td>$data[nama]</td>";
                echo "<td>$data[jobstatus]</td>";

                $qwe   = "SELECT id FROM absen WHERE activerow='1' AND nip=\"$data[nip]\" AND tanggal BETWEEN \"$_POST[tgl_mulai]\" AND \"$_POST[tgl_selesai]\" AND (status_in != 'NSI' AND status_out != 'NSO')";
                $doqwe = mysqli_query($conn, $qwe);                                      
                $total_kehadiran = mysqli_num_rows($doqwe);

                $qwe   = "SELECT id FROM absen WHERE activerow='1' AND nip=\"$data[nip]\" AND tanggal BETWEEN \"$_POST[tgl_mulai]\" AND \"$_POST[tgl_selesai]\" AND bekerja='WFH' AND (status_in != 'NSI' AND status_out != 'NSO')";
                $doqwe = mysqli_query($conn, $qwe);                                      
                $total_wfh = mysqli_num_rows($doqwe);
                
                $qwe   = "SELECT id FROM absen WHERE activerow='1' AND nip=\"$data[nip]\" AND tanggal BETWEEN \"$_POST[tgl_mulai]\" AND \"$_POST[tgl_selesai]\" AND bekerja='WFO' AND (status_in != 'NSI' AND status_out != 'NSO')";
                $doqwe = mysqli_query($conn, $qwe);                                      
                $total_wfo = mysqli_num_rows($doqwe);

                $qwe   = "SELECT SEC_TO_TIME(SUM(TIME_TO_SEC(TIMEDIFF(tap_in, '08:00:00')))) AS total_telat FROM absen WHERE activerow='1' AND nip=\"$data[nip]\" AND tanggal BETWEEN \"$_POST[tgl_mulai]\" AND \"$_POST[tgl_selesai]\" and tap_in > '08:00:00' and status_in ='LTI'";
                $doqwe = mysqli_query($conn, $qwe);
                $data  = mysqli_fetch_array($doqwe);
                $total_telat = ($data['total_telat'] ? $data['total_telat'] : 0);
                
                echo "<td>$total_kehadiran</td>";
                echo "<td>$total_wfh</td>";
                echo "<td>$total_wfo</td>";
                echo "<td>$total_telat</td>";
                echo "</tr>";
            }

            $tgl_mulai   = date("d M Y", strtotime($_POST['tgl_mulai']));
            $tgl_selesai = date("d M Y", strtotime($_POST['tgl_selesai']));
            if ($tgl_mulai == $tgl_selesai) {
                $tglfile = $tgl_mulai;
            }
            else{
                $tglfile = $tgl_mulai." - ".$tgl_selesai;
            }
